import"./firebase-CSiidJbk.js";document.addEventListener("DOMContentLoaded",function(){const n=document.getElementById("login-form");if(!n){console.error('Login form with id "login-form" not found.');return}const o=document.createElement("div");o.classList.add("error-message-container"),n.prepend(o);function a(t){o.innerHTML=`<p>${t}</p>`}function c(){o.innerHTML=""}n.addEventListener("submit",function(t){t.preventDefault(),c();const s=n.elements.email.value,i=n.elements.password.value;if(!s||!i){a("メールアドレスとパスワードを入力してください。");return}window.signInWithEmailPassword(s,i).then(r=>{console.log("Login successful",r.user),window.location.href="/index.html"}).catch(r=>{console.error("Login error",r);let e="ログインに失敗しました。";switch(r.code){case"auth/user-not-found":case"auth/wrong-password":case"auth/invalid-credential":e="メールアドレスまたはパスワードが間違っています。";break;case"auth/invalid-email":e="メールアドレスの形式が正しくありません。";break;case"auth/user-disabled":e="このアカウントは無効化されています。";break;case"auth/too-many-requests":e="試行回数が多すぎます。後でもう一度お試しください。";break;default:e="エラーが発生しました。しばらくしてから再度お試しください。"}a(e)})})});
